import React, {useEffect, useState ,useContext} from 'react';
import Avatar from '@material-ui/core/Avatar';
import Button from '@material-ui/core/Button';
import CssBaseline from '@material-ui/core/CssBaseline';
// import TextField from '@material-ui/core/TextField';
import Link from '@material-ui/core/Link';
import { Route } from 'react-router-dom';
// import Grid from '@material-ui/core/Grid';
// import Box from '@material-ui/core/Box';
import LockOutlinedIcon from '@material-ui/icons/LockOutlined';
import Typography from '@material-ui/core/Typography';
import Grid from '@material-ui/core/Grid';
import Switch from '@material-ui/core/Switch';
import { makeStyles } from '@material-ui/core/styles';
import Container from '@material-ui/core/Container';
import { UserContext } from "../../UserContext";
import axios from "axios";
import { ValidatorForm, TextValidator} from 'react-material-ui-form-validator';
import red from '@material-ui/core/colors/red';
import { useHistory } from "react-router-dom";
import {validateSpecialCharacters, validateEmail} from "views/functions.js";
import {BlankArea} from "CustomComponents/CustomComponents.js"
import { useParams } from "react-router";

// const [myGroupName, setMyGroupName] = useState("");

const useStyles = makeStyles((theme) => ({
  paper: {
    marginTop: theme.spacing(8),
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
  },
  avatar: {
    margin: theme.spacing(1),
    backgroundColor: theme.palette.secondary.main,
  },
  form: {
    width: '100%', // Fix IE 11 issue.
    marginTop: theme.spacing(1),
  },
  submit: {
    margin: theme.spacing(3, 0, 2),
  },
  error:  {
      // right: 0,
      fontSize: '12px',
      color: red[700],
      // position: 'absolute',
      alignItems: 'center',
      marginTop: '0px',
  },
}));

var myGroupName = "";
var myDisplayName = "";
var myAdmin = false;
var myAdminSwitch = false;
var myCurrent = false;
var myDefault = false;



class ChildComp extends React.Component {

  componentDidMount()  {
    // custom rule will have name 'isPasswordMatch'
    ValidatorForm.addValidationRule('isPasswordMatch', (value) => {
      return (value === this.props.p1)
    });

    ValidatorForm.addValidationRule('minLength', (value) => {
      return (value.length >= 6)
    });

    ValidatorForm.addValidationRule('noSpecialCharacters', (value) => {
      return validateSpecialCharacters(value);
    });

    ValidatorForm.addValidationRule('isEmailOK', (value) => {
      return validateEmail(value);
    });
  }

  
  componentWillUnmount() {
    // remove rule when it is not needed
    ValidatorForm.removeValidationRule('isPasswordMatch');
    ValidatorForm.removeValidationRule('isEmailOK');
    ValidatorForm.removeValidationRule('minLength');
    ValidatorForm.removeValidationRule('noSpecialCharacters');   
  }

  render() {
    return <br/>;
  }

}
// const handleSubmit = e => {
//   e.preventDefault();
// };



export default function GroupDetails() {
  const classes = useStyles();
  const history = useHistory();
//   const [userName, setUserName] = useState("");
//   const [password, setPassword] = useState("");
//   const [email, setEmail] = useState("");
//   const [repeatPassword, setRepeatPassword] = useState("");
  const [registerStatus, setRegisterStatus] = useState(0);
const [displayName, setDisplayName] = useState("");

  const { setUser } = useContext(UserContext);

  useEffect(() => {
    myGroupName = window.localStorage.getItem("gdName")
    myDisplayName = window.localStorage.getItem("gdDisplay")
    setDisplayName(myDisplayName);
    // console.log(window.localStorage.getItem("gdAdmin"))
    myAdmin = (window.localStorage.getItem("gdAdmin").toLowerCase() === "admin") ? "Admin" : "Not Admin";
    myAdminSwitch = (window.localStorage.getItem("gdAdmin").toLowerCase() === "admin");
    myCurrent = (window.localStorage.getItem("gdCurrent").toLowerCase() === "true");
    myDefault = (window.localStorage.getItem("gdDefault").toLowerCase() === "true");
  }, [])


  const handleSubmit = async() => {
    console.log("Submit command provided");
    console.log(myDisplayName);
  }

  function handleLogin() {
    // console.log("Call for login here");
    history.push("/signin")
  }

  function ShowResisterStatus() {
    // console.log(`Status is ${registerStatus}`);
    let myMsg;
    switch (registerStatus) {
      case 200:
        // setUserName("");
        // setPassword("");
        // setRepeatPassword("");
        // setEmail("");
        myMsg = `User ${userName} successfully regisitered.`;
        break;
      case 602:
        myMsg = "User Name already in use";
        break;
      case 603:
        myMsg = "Email id already in use";
        break;
      default:
          myMsg = "";
          break;
    }
    return(
        <Typography className={(registerStatus === 200) ? classes.root : classes.error}>{myMsg}</Typography>
    )
  }

  function handleChange() {
      console.log("Admin swithc")
  }
  function AdminSwitch() {
    return (
        // <Typography component="div">
        // <Grid component="label" container alignItems="center" spacing={1}>
        //   <Grid item>Admin</Grid>
        //   <Grid item>
        //     {/* <Switch color="primary" checked={myAdminSwitch} name="adminSw" /> */}
            <Switch
                checked={true}
                onChange={handleChange}
                color="primary"
                name="checkedB"
                // inputProps={{ 'aria-label': 'primary checkbox' }}
            />
    //         </Grid>
    //       <Grid item>Not Admin</Grid>
    //     </Grid>
    //   </Typography>
    )
  }

  return (
    <Container component="main" maxWidth="xs">
      <CssBaseline />
      <div className={classes.paper}>
        <Typography component="h1" variant="h5">
          Group Details
        </Typography>
    <ValidatorForm className={classes.form} onSubmit={handleSubmit}>
      <TextValidator
          variant="outlined"
          fullWidth      
          label="Group Name (Read-Only)"
          readOnly
          value={myGroupName}
        />
      <BlankArea/>
      <TextValidator
          variant="outlined"
        //   required
          fullWidth      
          label="Franchise Name"
          onChange={(event) => setDisplayName(event.target.value)}
          name="displayname"
          validators={['noSpecialCharacters']}
          errorMessages={['Special characters not permitted']}
          value={displayName}
      />
      <BlankArea/>
      <TextValidator
          variant="outlined"
          fullWidth      
          label="Group Admin or Not (Read-Only)"
          readOnly
          value={myAdmin}
        />
      <BlankArea/>
      <AdminSwitch />
      <BlankArea/>
        <div align="center">
      <Button
        type="submit"
        // size="small"
        // fullWidth
        variant="contained"
        color="primary"
        className={classes.submit}
      >
        Update
    </Button>
    <Button
        type="cancel"
        // fullWidth
        variant="contained"
        color="primary"
        className={classes.submit}
        onClick={() => {history.push('/admin/mygroup')}}
      >
        Back
    </Button>
    </div>
    </ValidatorForm>
    <ShowResisterStatus/>
    </div>
    {/* <ChildComp p1={password}/>     */}
    {/* <Switch>
      <Route  path='/admin/group' component={SignIn} key="MemberList"/>
    </Switch> */}
    </Container>
  );
}

