import React, { useEffect, useState } from 'react';
import clsx from 'clsx';
import Card from '@material-ui/core/Card';
import CardHeader from '@material-ui/core/CardHeader';
import CardMedia from '@material-ui/core/CardMedia';
import CardContent from '@material-ui/core/CardContent';
import CardActions from '@material-ui/core/CardActions';
import Collapse from '@material-ui/core/Collapse';
import Avatar from '@material-ui/core/Avatar';
import IconButton from '@material-ui/core/IconButton';
import Grid from "@material-ui/core/Grid";
import Table from "components/Table/Table.js";
import GridItem from "components/Grid/GridItem.js";
import Accordion from '@material-ui/core/Accordion';
import AccordionSummary from '@material-ui/core/AccordionSummary';
import AccordionDetails from '@material-ui/core/AccordionDetails';
import Typography from '@material-ui/core/Typography';
import ExpandMoreIcon from '@material-ui/icons/ExpandMore';
import { makeStyles } from '@material-ui/core/styles';
// import styles from "assets/jss/material-dashboard-react/views/dashboardStyle.js";
import socketIOClient from "socket.io-client";
import { NoGroup, DisplayPageHeader } from 'CustomComponents/CustomComponents.js';
// import {socketPoint} from "views/functions.js";
import { blue, red } from '@material-ui/core/colors';


const useStyles = makeStyles((theme) => ({
  root: {
    width: '100%',
  },
  heading: {
    fontSize: theme.typography.pxToRem(20),
    fontWeight: theme.typography.fontWeightBold,
    color: blue[800],
  },
  media: {
    height: 0,
    paddingTop: '56.25%', // 16:9
  },
  expand: {
    transform: 'rotate(0deg)',
    marginLeft: 'auto',
    transition: theme.transitions.create('transform', {
      duration: theme.transitions.duration.shortest,
    }),
  },
  expandOpen: {
    transform: 'rotate(180deg)',
  },
  avatar: {
    backgroundColor: red[500],
  },
}));
  

export default function About() { 
  const classes = useStyles();
  // const [expanded, setExpanded] = React.useState(false);

  const handleExpandClick = () => {
    setExpanded(!expanded);
  };


  useEffect(() => {  
    return () => {
    }
  }, []);


  const [expandedPanel, setExpandedPanel] = useState(false);
  const handleAccordionChange = (panel) => (event, isExpanded) => {
    // console.log({ event, isExpanded });
    setExpandedPanel(isExpanded ? panel : false);
  };

  function AboutAPL() {
  return (
    <Card className={classes.root}>
    <CardContent>
    <Typography paragraph>Method:</Typography>
    <Typography paragraph>
      Heat 1/2 cup of the broth in a pot until simmering, add saffron and set aside for 10
      minutes.
    </Typography>
    <CardMedia
        className={classes.media}
        image="APLLOGO.JPG"
        title="How to Play"
      />
    <Typography paragraph>
      Heat oil in a (14- to 16-inch) paella pan or a large, deep skillet over medium-high
      heat. Add chicken, shrimp and chorizo, and cook, stirring occasionally until lightly
      browned, 6 to 8 minutes. Transfer shrimp to a large plate and set aside, leaving chicken
      and chorizo in the pan. Add pimentón, bay leaves, garlic, tomatoes, onion, salt and
      pepper, and cook, stirring often until thickened and fragrant, about 10 minutes. Add
      saffron broth and remaining 4 1/2 cups chicken broth; bring to a boil.
    </Typography>
    <Typography paragraph>
      Add rice and stir very gently to distribute. Top with artichokes and peppers, and cook
      without stirring, until most of the liquid is absorbed, 15 to 18 minutes. Reduce heat to
      medium-low, add reserved shrimp and mussels, tucking them down into the rice, and cook
      again without stirring, until mussels have opened and rice is just tender, 5 to 7
      minutes more. (Discard any mussels that don’t open.)
    </Typography>
    <Typography>
      Set aside off of the heat to let rest for 10 minutes, and then serve.
    </Typography>
    </CardContent>
    </Card>
  )}

  function HowtoPlay() {
    return (
      <Card className={classes.root}>
      <CardContent>
      <Typography paragraph>Method:</Typography>
      <Typography paragraph>
        Heat 1/2 cup of the broth in a pot until simmering, add saffron and set aside for 10
        minutes.
      </Typography>
      <Typography paragraph>
        Heat oil in a (14- to 16-inch) paella pan or a large, deep skillet over medium-high
        heat. Add chicken, shrimp and chorizo, and cook, stirring occasionally until lightly
        browned, 6 to 8 minutes. Transfer shrimp to a large plate and set aside, leaving chicken
        and chorizo in the pan. Add pimentón, bay leaves, garlic, tomatoes, onion, salt and
        pepper, and cook, stirring often until thickened and fragrant, about 10 minutes. Add
        saffron broth and remaining 4 1/2 cups chicken broth; bring to a boil.
      </Typography>
      <Typography paragraph>
        Add rice and stir very gently to distribute. Top with artichokes and peppers, and cook
        without stirring, until most of the liquid is absorbed, 15 to 18 minutes. Reduce heat to
        medium-low, add reserved shrimp and mussels, tucking them down into the rice, and cook
        again without stirring, until mussels have opened and rice is just tender, 5 to 7
        minutes more. (Discard any mussels that don’t open.)
      </Typography>
      <Typography>
        Set aside off of the heat to let rest for 10 minutes, and then serve.
      </Typography>
      </CardContent>
      </Card>
    )}

  function DisplayPoint(props) {
  return (
  <div>
  <Grid key={props.key} container>
  <Grid item xs={6} sm={6} md={6} lg={6} >
  <Typography paragraph>{props.key}</Typography>
  </Grid>
  <Grid item xs={6} sm={6} md={6} lg={6} >
  <Typography paragraph>{props.value}</Typography>
  </Grid>
  </Grid>

  </div> 
  )}

  function PointSystem() {
  return (
    <Card className={classes.root}>
    <CardContent>
    <Typography paragraph>Batting Points:</Typography>
    <DisplayPoint key="●	Run" value="1 point"/>
    <DisplayPoint key="● Boundary bonus" value="1 point" />
    <DisplayPoint key="● Six bonus" value="2 point" />
    <DisplayPoint key="● Half century bonus" value="20 point" />
    <DisplayPoint key="● Century bonus" value="50 point" />
    <DisplayPoint key="● Duck Penalty" value="-2 point" />
    <Typography paragraph>
    Note: Any player scores century in a match will only 
    get bonus point of century no extra 
    bonus point for half-century.
    </Typography>
    <Typography paragraph>Bowling points:</Typography>
    <Typography paragraph>
    ●	Wicket (excluding run out) 25 points
    ●	3 wicket bonus              20 points
    ●	5 wicket bonus              50 points
    </Typography>
    <Typography paragraph>
    Note : Any if player gets 5 wicket in a match you will only
    get the bonuspoint of 5 wicket haul. No extra bonus points
    for 3 wicket haul. 
    </Typography>
    </CardContent>
    </Card>
  )}
  
  function DisplayHeader(props) {
  return(
    <AccordionSummary expandIcon={<ExpandMoreIcon />} aria-controls="panel1a-content" id="panel1a-header">
    <Typography className={classes.heading}>{props.header}</Typography>
    </AccordionSummary>   
  )}

  return (
    <div className={classes.root}>
      <DisplayPageHeader headerName="Help Desk" groupName="" tournament=""/>
      <Accordion expanded={expandedPanel === "about"} onChange={handleAccordionChange("about")}>
        <DisplayHeader header="Auction Permier League"/>
        <AccordionDetails>
        <AboutAPL />
        </AccordionDetails>
      </Accordion>
      <Accordion expanded={expandedPanel === "HowtoPlay"} onChange={handleAccordionChange("HowtoPlay")}>
        <DisplayHeader header="How to Play" />
        <AccordionDetails>
        <HowtoPlay />
