return (
    <div key={localStorage.getItem("gdName")}>
        <h3 className={classes.cardTitle}>Edit Member List ({localStorage.getItem("gdName")})</h3>
        <ShowFilters/>
        <TableContainer>
        <Table
            className={classes.table}
            // aria-labelledby="tableTitle"
            size={dense ? 'small' : 'medium'}
            // aria-label="enhanced table"
          >
        <EnhancedTableHead/>
        <TableBody>
              {userArray.slice(page * rowsPerPage, page * rowsPerPage + rowsPerPage)
                // .filter(x => x.displayName.toLowerCase().includes(filterString))
                .map((row, index) => {
                  const labelId = `enhanced-table-checkbox-${index}`;
                  return (
                    <TableRow
                      // hover
                      onClick={(event) => handleClick(event, row.uid)}
                      role="checkbox"
                    //   aria-checked={(selectedStation === row.userName)}
                      tabIndex={-1}
                      key={row.uid}
