import React, { useEffect, useState } from 'react';
import axios from "axios";

import Grid from "@material-ui/core/Grid";
import Table from "components/Table/Table.js";
import GridItem from "components/Grid/GridItem.js";
import Accordion from '@material-ui/core/Accordion';
import AccordionSummary from '@material-ui/core/AccordionSummary';
import AccordionDetails from '@material-ui/core/AccordionDetails';
import Typography from '@material-ui/core/Typography';
import ExpandMoreIcon from '@material-ui/icons/ExpandMore';

import { makeStyles } from '@material-ui/core/styles';
// import styles from "assets/jss/material-dashboard-react/views/dashboardStyle.js";
import { NoGroup, DisplayPageHeader } from 'CustomComponents/CustomComponents.js';

const useStyles = makeStyles((theme) => ({
    root: {
      width: '100%',
    },
    heading: {
      fontSize: theme.typography.pxToRem(15),
      fontWeight: theme.typography.fontWeightRegular,
    },
  }));
  

function leavingStatistics(myConn) {
  console.log("Leaving Statistics wah wah ");
  myConn.disconnect();
}

export default function MatchInfo() { 
  const classes = useStyles();
  // const [teamArray, setTeamArray] = useState([]);
  // axios.get(`/stat/sendmystat/${localStorage.getItem("gid")}`);

  useEffect(() => {  
    const [tournamentArray, setTournamentArray] = useState([]);

    const fetchTournament = async () => {
      try {
          var response = await axios.get(`/tournamet/tournament`);
          console.log(response.data);
          setTournamentArray(response.data);
      } catch (e) {
          console.log(e)
      }
    }
    if (hasGroup()) fetchTournament();
  }, []);



  const [expandedPanel, setExpandedPanel] = useState(false);
  const handleAccordionChange = (panel) => (event, isExpanded) => {
    // console.log({ event, isExpanded });
    setExpandedPanel(isExpanded ? panel : false);
  };

  function ShowTournaments() {  
      return (tournamentArray.map(ttt =>
      <Accordion expanded={expandedPanel === ttt.tournament} onChange={handleAccordionChange(ttt.tournament)}>
          <AccordionSummary expandIcon={<ExpandMoreIcon />} aria-controls="panel1a-content" id="panel1a-header">
              <Typography className={classes.heading}>{ttt.tournament} ({ttt.type})</Typography>
          </AccordionSummary> 
          <AccordionDetails>
              <Grid container justify="center" alignItems="center" >
                  <GridItem xs={12} sm={12} md={12} lg={12} >
                      <Table
                          tableHeaderColor="warning"
                          tableHead={["Team1", "Team2", "Time"]}
                          tableData={ttt.matches.map(mmm => {
                              const arr = [mmm.team1, mmm.team2, mmm.matchStartTime]
                              return { data: arr, collapse: [] }
                          })}
                      />
                  </GridItem>
              </Grid>
          </AccordionDetails>
      </Accordion>
      ))
  }

  return (
    <div className={classes.root}>
      {/* <h3 align="center">Statistics of {localStorage.getItem("tournament")}</h3>  */}
      <DisplayPageHeader headerName="Tournaments" groupName={localStorage.getItem("groupName")} tournament={localStorage.getItem("tournament")}/>
      <ShowTournaments/>
    </div>
  );
};
