  // select tournament and filters teams of the given tournament
  function selectTournamentName(myTournament) {
    console.log(`Selected ${myTournament}`)
    let tmp = masterTeamList.filter(x => x.tournament === myTournament);
    // console.log(tmp);
    setTeamList(tmp);
