      <DisplayPlayer />
      <Typography className={classes.root}>
      <Link href="#" onClick={handleAddNewPlayer} variant="body2">Add New Player</Link>
      </Typography>
      <ShowResisterStatus/>
      <BlankArea/>
      <Button type="submit" key={"create"} variant="contained" color="primary" 
