import React, { useEffect, useState, useContext } from 'react';
import axios from "axios";
import { makeStyles } from '@material-ui/core/styles';
// import { Switch, Route } from 'react-router-dom';
import Container from '@material-ui/core/Container';
import CssBaseline from '@material-ui/core/CssBaseline';
import { ValidatorForm, TextValidator} from 'react-material-ui-form-validator';
import Select from "@material-ui/core/Select";
import MenuItem from '@material-ui/core/MenuItem';
import Link from '@material-ui/core/Link';
import Button from '@material-ui/core/Button';
import Table from "components/Table/Table.js";
import FormControlLabel from '@material-ui/core/FormControlLabel';
import Radio from '@material-ui/core/Radio';
import Card from "components/Card/Card.js";
import CardBody from "components/Card/CardBody.js";
import Grid from "@material-ui/core/Grid";
import GridItem from "components/Grid/GridItem.js";
import Accordion from '@material-ui/core/Accordion';
import AccordionSummary from '@material-ui/core/AccordionSummary';
import AccordionDetails from '@material-ui/core/AccordionDetails';
import Typography from '@material-ui/core/Typography';
import ExpandMoreIcon from '@material-ui/icons/ExpandMore';
import { useHistory } from "react-router-dom";
import { UserContext } from "../../UserContext";
import { getImage } from "views/functions.js"
import {DisplayPageHeader, ValidComp, BlankArea, NothingToDisplay, DisplayBalance} from "CustomComponents/CustomComponents.js"
import {red, blue, green } from '@material-ui/core/colors';
import {setTab} from "CustomComponents/CricDreamTabs.js"
const rPrefix = "radio-";

const useStyles = makeStyles((theme) => ({
    root: {
      width: '100%',
    }, 
    info: {
        color: blue[700],
    },     
    header: {
        color: '#D84315',
    }, 
    error:  {
      // right: 0,
      fontSize: '12px',
      color: red[700],
      // position: 'absolute',
      alignItems: 'center',
      marginTop: '0px',
  },    
    messageText: {
          color: '#4CC417',
          fontSize: 12,
          // backgroundColor: green[700],
    },
    symbolText: {
        color: '#4CC417',
        // backgroundColor: green[700],
    },
    button: {
        margin: theme.spacing(0, 1, 0),
    },
    heading: {
      fontSize: theme.typography.pxToRem(15),
      flexBasis: '33.33%',
      flexShrink: 0,
    },
    secondaryHeading: {
      fontSize: theme.typography.pxToRem(15),
      color: theme.palette.text.secondary,
    },
  }));


export default function SU_Tournament() {
  const [tournamentName, setTournamentName] = useState("");
  const [tournamentType, setTournamentType] = useState("T20");
  const [tournamentData, setTournamentData] = useState(["T20", "ODI", "TEST"]);
  const [teamList, setTeamList] = useState([]);
  const [registerStatus, setRegisterStatus] = useState(199);
  const classes = useStyles();
      
  useEffect(() => {
      const a = async () => {
        var teamres = await axios.get(`/team/list/`);
        setTeamList(teamres.data);
      }
      a();
  }, [])


  let newTeamList = [
    {label: "TEAM1", existingTeam: true, name: "INDIA"},
    {label: "TEAM2", existingTeam: true, name: "ENGLAND"},
    {label: "TEAM3", existingTeam: true, name: ""},
  ];
  
  const [expandedPanel, setExpandedPanel] = useState(false);
  const handleAccordionChange = (panel) => (event, isExpanded) => {
    // console.log({ event, isExpanded });
    setExpandedPanel(isExpanded ? panel : false);
  };
  
  function DisplayTeam() {
    return (newTeamList.map(team =>
    <Accordion expanded={expandedPanel === team.label} onChange={handleAccordionChange(team.label)}>
      <AccordionSummary expandIcon={<ExpandMoreIcon />} aria-controls="panel1a-content" id="panel1a-header">
          <Typography className={classes.heading}>{team.name} ({team.existingTeam})</Typography>
      </AccordionSummary>
      <AccordionDetails>
          <Grid container justify="center" alignItems="center" >
              <GridItem xs={12} sm={12} md={12} lg={12} >
              </GridItem>
          </Grid>
      </AccordionDetails>
    </Accordion>
    ));
  }
  
      
  function handleSubmit() {
      console.log("Submit Clicked");
  }

  function ShowResisterStatus() {
    // console.log(`Status is ${registerStatus}`);
    let myMsg;
    let errmsg = true;
    switch (registerStatus) {
      case 199:
        myMsg = `Error 199`;
        errmsg = false;
        break;      
      case 200:
        myMsg = `User successfully regisitered.`;
        errmsg = false;
        break;
      case 629:
        myMsg = "Invalid Tournament";
        break;
      default:
        myMsg = "unknown error";
        break;
    }
    let myClass = (errmsg) ? classes.error : classes.root;
    return(
      <div>
        <Typography className={myClass}>{myMsg}</Typography>
      </div>
    );
  }

  function DisplaySingleTeam() {
  return(
    <RadioGroup aria-label="quiz" name="quiz" value={value} onChange={handleRadioChange}>
      <FormControlLabel value="" control={<Radio />} label="The best!" />
      <FormControlLabel value="worst" control={<Radio />} label="The worst." />
    </RadioGroup>
  )}

  return (
  <div className={classes.paper} align="center" key="groupinfo">
      <DisplayPageHeader headerName="Configure New Tournament" groupName="" tournament=""/>
      <Container component="main" maxWidth="xs">
      <CssBaseline />
      <ValidatorForm className={classes.form} onSubmit={handleSubmit}>
      <BlankArea/>
      <TextValidator
        variant="outlined"
        required
        fullWidth      
        label="Tournament Name"
        onChange={(event) => setTournamentName(event.target.value)}
        name="groupname"
        // type=""
        validators={['required', 'minLength', 'noSpecialCharacters']}
        errorMessages={['', 'Tournament Name should be of minimum 6 characters', 'Special characters not permitted']}
        value={tournamentName}
      />
      <BlankArea/>
      <Select labelId='tournamentTypr' id='tournamentTypr'
        variant="outlined"
        required
        fullWidth
        label="Tournament Type"
        name="tournamentType"
        id="tournamenttype"
        value={tournamentType}
        //displayEmpty 
        onChange={(event) => setTournamentType(event.target.value)}
        >
        {tournamentData.map(x =>
        <MenuItem key={x} value={x}>{x}</MenuItem>)}
      </Select>
      {/* <DisplayTeam /> */}
      <ShowResisterStatus/>
      <BlankArea/>
      <Button type="submit" key={"create"} variant="contained" color="primary" size="small"
          className={classes.button}>Create
      </Button>
      </ValidatorForm>
      <ValidComp />   
      </Container>
  </div>
  );    
}
