// const ENDPOINT = "https://happy-home-ipl-2020.herokuapp.com/";
export const ENDPOINT = "http://localhost:4000";

export function socketPoint() {
	return ENDPOINT;
}

export function validateSpecialCharacters(sss) {
    var sts = false;
    if (!sss.includes("\\"))
    if (!sss.includes("^"))
    if (!sss.includes("~"))
    if (!sss.includes("/"))
      sts = true;
    return sts;
}
export function aruntesting(hsvH, hsvS, hsvV) {
	const hslL = (200 - hsvS) * hsvV / 100;

	const [ hslS, hslV ] = [
		hslL === 0 || hslL === 200 ? 0 : hsvS * hsvV / 100 / (hslL <= 100 ? hslL : 200 - hslL) * 100,
		hslL * 5 / 10
	];

	return [ hsvH, hslS, hslV ];
}
