  async function handleCountChange(event) {
    let idx = parseInt(event.target.value);
    let xxx = axios.get(`${process.env.REACT_APP_AXIOS_BASEPATH}/group/setprize/${newGid}/${idx}`);
    setPrizeTable(masterPrizeTable[idx-1])
    setPrizeCount(idx);
    await xxx;
  };

