import React, { useEffect, useState, useContext } from 'react';
import axios from "axios";
import { makeStyles, useTheme } from '@material-ui/core/styles';
import DialogTitle from '@material-ui/core/DialogTitle';
import Dialog from '@material-ui/core/Dialog';

import Accordion from '@material-ui/core/Accordion';
import AccordionSummary from '@material-ui/core/AccordionSummary';
import AccordionDetails from '@material-ui/core/AccordionDetails';
import Typography from '@material-ui/core/Typography';
import ExpandMoreIcon from '@material-ui/icons/ExpandMore';

import MenuItem from '@material-ui/core/MenuItem';
import FormControl from '@material-ui/core/FormControl';
import DoneIcon from '@material-ui/icons/Done';

// import NavigateBeforeIcon from '@material-ui/icons/NavigateBefore';
// import NavigateNextIcon from '@material-ui/icons/NavigateNext';
//import Container from "@material-ui/core/Container";

import Select from "@material-ui/core/Select";
import Table from "components/Table/Table.js";
import Grid from "@material-ui/core/Grid";
import Button from '@material-ui/core/Button';

import DialogActions from '@material-ui/core/DialogActions';
// import DialogContent from '@material-ui/core/DialogContent';
// import DialogContentText from '@material-ui/core/DialogContentText';


import GridItem from "components/Grid/GridItem.js";

import Drawer from '@material-ui/core/Drawer';
import ChevronLeftIcon from '@material-ui/icons/ChevronLeft';
import ChevronRightIcon from '@material-ui/icons/ChevronRight';
import IconButton from '@material-ui/core/IconButton';
// import CheckSharpIcon from '@material-ui/icons/CheckSharp';
// import ClearSharpIcon from '@material-ui/icons/ClearSharp';
// import Avatar from "@material-ui/core/Avatar"


import Card from "components/Card/Card.js";
// import CardAvatar from "components/Card/CardAvatar.js";
import CardBody from "components/Card/CardBody.js";
import { UserContext } from "../../UserContext";

// import socketIOClient from "socket.io-client";
// const ENDPOINT = "https://happy-home-ipl-2020.herokuapp.com/";
// const ENDPOINT = "http://localhost:4000";

const useStyles = makeStyles((theme) => ({
    root: {
      width: '100%',
    },
    heading: {
      fontSize: theme.typography.pxToRem(15),
      flexBasis: '33.33%',
      flexShrink: 0,
    },
    secondaryHeading: {
      fontSize: theme.typography.pxToRem(15),
      color: theme.palette.text.secondary,
    },
  }));


export default function ImgMediaCard() {

    window.onbeforeunload = () => setUser(null)

    const { setUser } = useContext(UserContext);
    const classes = useStyles();
    const theme = useTheme();
    const [open, setOpen] = useState(false);
    const [confirmDialogOpen, setConfirmDialogOpen] = useState(false)
    const [selectedOwner, setSelectedOwner] = useState(null);

    const [backDropOpen, setBackDropOpen] = useState(false);
    const [playerStatus, setPlayerStatus] = useState();
    const [captainTableData, setCaptainTableData] = useState([{desc: "Captain", name: ""},
        {desc: "ViceCaptain", name: ""}]);
    const [myTeamTableData, setMyTeamTableData] = useState([]);
    const [isUserMemberOfGroup, setGroupMember] = useState(false);
    const [tournamentStated, setTournamentStarted] = useState(false);
    const [selectWhat, setSelectWhat] = useState("Captain");
    const [selectedCaptain, setSelectedCaptain] = useState("");
    const [selectedViceCaptain, setSelectedViceCaptain] = useState("");

    useEffect(() => {
        const a = async () => {
        // get start of tournamnet (i.e. start of 1st match)
        var gameStarted = false;  
        var mygroup  = localStorage.getItem("gid")
        if  ((mygroup === "") || (mygroup === "0")) {
            // handle if not a member of any group
            setGroupMember(false);
            setTournamentStarted(false);
            console.log("Not a member of group. No cap/vice cap")
            return;
        }
          

        // user belong to group. get cpation / vice captian alreasy set
        // let response = ""
        var response = await axios.get(`/user/getcaptain/${mygroup}/${localStorage.getItem("uid")}`);
        // var isGroupMember = false;
        if (response.data.length > 0) {
            captainTableData[0].name = response.data[0].captainName;
            captainTableData[1].name = response.data[0].viceCaptainName;
        }

        response = await axios.get(`/group/gamestarted/${localStorage.getItem("gid")}`);
        gameStarted = (response.data.length > 0);
        gameStarted = false;
        if (!gameStarted) {
            // get list of player purchased by user for aelecting captain / vice captain
            var myUrl = `/user/myteamwos/${mygroup}/${localStorage.getItem("uid")}`;
            const teamResponse = await axios.get(myUrl);
            // console.log(teamResponse.data);
            console.log(teamResponse.data[0].players);
            setMyTeamTableData(teamResponse.data[0].players);
        }         
        setCaptainTableData(captainTableData);
        setTournamentStarted(gameStarted);
        setGroupMember(true);
    }

    a();
}, [selectedCaptain])


const handleCaptainChange = (event) => {
    setSelectedCaptain(event.target.value);
    console.log(`Caption: ${event.target.value}`);
    // captainTableData[0].name = event.target.value
    // setCaptainTableData(captainTableData);        
};
 
const updateNewCaptain = () => {
    console.log(`Updating button: ${selectedCaptain}`)
    captainTableData[0].name = selectedCaptain;
    setCaptainTableData(captainTableData);       
};

const handleViceCaptainChange = (event) => {
    setSelectedViceCaptain(event.target.value);
    // captainTableData[1].name = event.target.value
    // setCaptainTableData(captainTableData);
};

function ShowSummary(props) {
return (
    <div>
        <Typography className={classes.heading}>{props.desc}</Typography>
        <Typography className={classes.secondaryHeading}>{props.name}</Typography>
    </div>)
}
    
const [expandedPanel, setExpandedPanel] = useState(false);
const handleAccordionChange = (panel) => (event, isExpanded) => {
    setExpandedPanel(isExpanded ? panel : false);
};
    
function ShowCaptainViceCaptain() {
    // if (tournamentStated)
    if (false)
    return (
    <div>
    <Accordion>
        <AccordionSummary expanded={false} id="panel1a-header">
            <ShowSummary desc={captainTableData[0].desc} name={captainTableData[0].name}/>
        </AccordionSummary>
    </Accordion>
    <Accordion>
        <AccordionSummary expanded={false} id="panel1a-header">
        <ShowSummary desc={captainTableData[1].desc} name={captainTableData[1].name}/>
        </AccordionSummary>
    </Accordion>
    </div>
    );
    else {
    setSelectedCaptain(captainTableData[0].name);
    setSelectedViceCaptain(captainTableData[1].name);
    return (
    <div>
    <Accordion expanded={expandedPanel === captainTableData[0].desc} onChange={handleAccordionChange(captainTableData[0].desc)}>
        <AccordionSummary expandIcon={<ExpandMoreIcon />} aria-controls="panel1a-content" id="panel1a-header">
            <ShowSummary desc={captainTableData[0].desc} name={captainTableData[0].name}/>
        </AccordionSummary>
        <AccordionDetails>
            <div>
                <Select
                    labelId="demo-simple-select-label"
                    id="demo-simple-select"
                    value={selectedCaptain}m   
                    onChange={handleCaptainChange}
                    displayEmpty>                         
                    {myTeamTableData.map(item => <MenuItem key={item.pid} value={item.playerName}>{item.playerName}</MenuItem>)}
                </Select>
            </div>
            <Button onClick={updateNewCaptain} color="primary">Select Captain</Button>
        </AccordionDetails>
    </Accordion>
    <Accordion expanded={expandedPanel === captainTableData[1].desc} onChange={handleAccordionChange(captainTableData[1].desc)}>
        <AccordionSummary expandIcon={<ExpandMoreIcon />} aria-controls="panel1a-content" id="panel1a-header">
            <ShowSummary desc={captainTableData[1].desc} name={captainTableData[1].name}/>
        </AccordionSummary>
        <AccordionDetails>
        </AccordionDetails>
    </Accordion>
    </div>
    );
    }
}
    
        
    
if (isUserMemberOfGroup)
return (
    <div>
        <h3>My Captain and Vice Captain ({localStorage.getItem("tournament")})</h3>
        <ShowCaptainViceCaptain/>
    </div>
);
else 
