    useEffect(() => {


        const socket = socketIOClient(ENDPOINT);
        socket.on("connect", () => {

            console.log("client connected");


            socket.on("playerChange", async (newPlayerDetails, balanceDetails) => {

                setAuctionStatus("RUNNING");
                console.log("player change")

                let userBalance = [];
