import React, { useEffect, useState } from 'react';
import axios from "axios";
import { Switch, Route } from 'react-router-dom';
import Button from '@material-ui/core/Button';
import { useHistory } from "react-router-dom";
// import Grid from "@material-ui/core/Grid";
import Table from "components/Table/Table.js";
import { makeStyles } from '@material-ui/core/styles';
import TextField from '@material-ui/core/TextField';
import Checkbox from '@material-ui/core/Checkbox';
import Radio from '@material-ui/core/Radio';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TablePagination from '@material-ui/core/TablePagination';
import TableRow from '@material-ui/core/TableRow';
import TableSortLabel from '@material-ui/core/TableSortLabel';
// import GridItem from "components/Grid/GridItem.js";
// import Card from "components/Card/Card.js";
// import CardAvatar from "components/Card/CardAvatar.js";
// import CardBody from "components/Card/CardBody.js";
// import { useLocation } from "react-router-dom";
import GroupMember from "views/Group/GroupMember.js"
import NewGroup from "views/Group/NewGroup.js"

const drawerWidth = 100;
const useStyles = makeStyles((theme) => ({
    margin: {
        margin: theme.spacing(1),
    },
    image: {
        height: "200px"
    },
    container: {
        backgroundImage: "url(\"../RCB/5334.jpg\")",
        backgroundSize: 'cover'
    }, drawer: {
        width: drawerWidth,
        flexShrink: 0,
    },
    drawerPaper: {
        width: drawerWidth,
    },
    drawerHeader: {
        display: 'flex',
        alignItems: 'center',
        padding: theme.spacing(0, 1),
        // necessary for content to be below app bar
        ...theme.mixins.toolbar,
        justifyContent: 'flex-start',
    },
    sold: {
        color: "green"
    }, cardCategoryWhite: {
        color: "rgba(255,255,255,.62)",
        margin: "0",
        fontSize: "14px",
        marginTop: "0",
        marginBottom: "0"
    },
    cardTitleWhite: {
        color: "#FFFFFF",
        marginTop: "0px",
        minHeight: "auto",
        fontWeight: "300",
        fontFamily: "'Roboto', 'Helvetica', 'Arial', sans-serif",
        marginBottom: "3px",
        textDecoration: "none"
    }, 
    large: {
        width: theme.spacing(12),
        height: theme.spacing(12),
    },
    filter: {
        marginTop: '0.1rem',
        marginBottom: '0.1rem',
        fontSize: 5,
      },
    }));


export default function AddGroupMember() {
    const classes = useStyles();
    // const location = useLocation();
    const history = useHistory();
    const [memberArray, setMemberArray] = useState([]);
    const [userArray, setUserArray] = useState([]);
    const [tournamentStated, setTournamentStarted] = useState(false);
    const [filterString, setfilterString] = React.useState("");
    const [page, setPage] = React.useState(0);
    const [dense, setDense] = React.useState(true);
    const [rowsPerPage, setRowsPerPage] = React.useState(10);
    const [selectedStation, setSelectedStation] = React.useState("");

    useEffect(() => {
       
        const fetchMember = async () => {
            // console.log(state);
            try {
                var response = await axios.get(`/user/group/${localStorage.getItem("gdGid")}`);
                // console.log(response.data);
                setMemberArray(response.data);
                var response1 = await axios.get(`/user`);
                setUserArray(response1.data);
                // var gameStarted = (response1.data.length > 0);
                // console.log(gameStarted);
                // // gameStarted = false;
                // setTournamentStarted(gameStarted);
    
            } catch (e) {
                console.log(e)
            }
            setfilterString("")
        }
        // console.log(`Is Admin: ${localStorage.getItem("gdAdmin")}`);
        fetchMember();
    }, []);

    function addNewMember() {
        console.log("Add new member to be implemnted");
    }


    function ShowGmButtons() {
        return (
        <div align="center">
            <Button variant="contained" color="primary" size="small"
                disabled={tournamentStated || (localStorage.getItem("gdAdmin").length === 0)}
                className={classes.button} onClick={addNewMember}>Update List
            </Button>
            <Button variant="contained" color="primary" size="small"
                className={classes.button} onClick={() => { history.push("/admin/groupmember") }}>Back
            </Button>

        </div>)
    }

    function SetFilter() { 
        // console.log("in set filter");
        const chkstr = document.getElementById("filterstring").value.toLowerCase();
        setfilterString(chkstr);
        console.log(chkstr);
        // var tmp = masterData.filter(x => x.StationName.toLowerCase().includes(chkstr));
        // setStationTableData(tmp);
        // // setSelectedStation((tmp.length > 0) ? tmp[0].StationName : "");
        // setPage(0);
      }
    
    function ShowFilters() {
        return (
          <div className={classes.filter} align="right">
            <TextField className={classes.filter} id="filterstring" margin="none" size="small" defaultValue={filterString}/>        
            <Button key="filterbtn" variant="contained" color="primary" size="small"
              className={classes.button} onClick={SetFilter}>Filter
            </Button>
          </div>
        );
      }

    /*    
    return (
    <div key={localStorage.getItem("gdName")}>
        <h3 className={classes.cardTitle}>Edit Member List ({localStorage.getItem("gdName")})</h3>
        <ShowFilters/>
        <Table
            id={localStorage.getItem("gdName")}
            tableHeaderColor="warning"
            tableHead={["userName", "displayName"]}
            tableData={userArray.map(x => {
            const arr = [x.userName, x.displayName]
                return { data: arr, collapse: [] }
            })}
        />
        <br/>
        <ShowGmButtons/>
        <Switch>
            <Route  path='/admin/groupmember' component={GroupMember} key="MemberList"/>
            <Route  path='/admin/newgroup' component={NewGroup} key="NewGroup"></Route>
        </Switch>
    </div>
    );
*/

const handleChangePage = (event, newPage) => {
    event.preventDefault();
    setPage(newPage);
};

const handleChangeRowsPerPage = (event) => {
    setRowsPerPage(parseInt(event.target.value, 10));
    setPage(0);
};

const handleClick = (event, name) => {
    event.preventDefault();
    console.log(name);
    setSelectedStation(name);
  };

const emptyRows = rowsPerPage - Math.min(rowsPerPage, userArray.length - page * rowsPerPage);

return (
    <div key={localStorage.getItem("gdName")}>
        <h3 className={classes.cardTitle}>Edit Member List ({localStorage.getItem("gdName")})</h3>
        <ShowFilters/>
        <TableContainer>
        <TableBody>
              {userArray.slice(page * rowsPerPage, page * rowsPerPage + rowsPerPage)
                // .filter(x => x.StationName.toLowerCase().includes('r'))
                .map((row, index) => {
                  // const isItemSelected = isSelected(row.name);
                //   const isItemSelected = (selectedStation === row.StationName);
                  // console.log(`Info on ${selectedStation} and ${row.StationName}`)
                  // console.log(row);
                  const labelId = `enhanced-table-checkbox-${index}`;
                  return (
                    <TableRow
                      // hover
                      onClick={(event) => handleClick(event, row.userName)}
                      role="checkbox"
                      aria-checked={(selectedStation === row.userName)}
                      tabIndex={-1}
                      key={row.userName}
                      selected={(selectedStation === row.userName)}
                    >
                      {/* <TableCell padding="checkbox">
                        <Checkbox
                          checked={isItemSelected}
                          inputProps={{ 'aria-labelledby': labelId }}
                        />
                      </TableCell> */}
                      <TableCell padding="checkbox">
                        <Checkbox
                          color="primary"
                          checked={(selectedStation === row.userName)}
                          inputProps={{ 'aria-labelledby': labelId }}
                        />
                      </TableCell>
                      <TableCell component="th" id={labelId} scope="row" align="center" padding="none">
                        {row.userName}
                      </TableCell>
                      {/* <TableCell align="center">{row.analogCount}</TableCell>
                      <TableCell align="center">{row.statusCount}</TableCell> */}
                      {/* <TableCell align="right">{row.carbs}</TableCell>
                      <TableCell align="right">{row.protein}</TableCell> */}
                    </TableRow>
                  );
                })}
              {emptyRows > 0 && (
                <TableRow style={{ height: (dense ? 33 : 53) * emptyRows }}>
                  <TableCell colSpan={2} />
                </TableRow>
              )}
            </TableBody>
        </TableContainer>
        <TablePagination
          rowsPerPageOptions={[5, 10, 15, 20, 25]}
          component="div"
          count={userArray.length}
          rowsPerPage={rowsPerPage}
          page={page}
          onChangePage={handleChangePage}
          onChangeRowsPerPage={handleChangeRowsPerPage}
        />
        <br/>
        <ShowGmButtons/>
        <Switch>
